/** @jsx jsx */
import { jsx } from 'theme-ui';
import { Container, Grid } from 'theme-ui';
import SectionHeader from '../components/section-header';
import FeatureCardColumn from 'components/feature-card-column.js';
import Vector from 'assets/key-feature/vector.svg';
import Editing from 'assets/key-feature/editing.svg';
import Speed from 'assets/key-feature/speed.svg';

const data = [
  {
    id: 1,
    imgSrc: Vector,
    altText: 'create',
    title: 'Create',
    text: 'We Create',
  },
  {
    id: 2,
    imgSrc: Editing,
    altText: 'innovate',
    title: 'Innovate',
    text: 'We Are Innovative ',
  },
  {
    id: 3,
    imgSrc: Speed,
    altText: 'Execute',
    title: 'Execute',
    text: 'We bring ideas to Life',
  },

  {
    id: 3,
    imgSrc: Speed,
    altText: 'Execute',
    title: 'Execute',
    text: 'We bring ideas to Life',
    btnName: 'Learn More',
    subTitle: 'Investments',
    title: 'Partnerships',
    description:
      'Join Our Partnership program to Learn more about becoming a CixHub Partner in the rapidly growing CixHub Ecosystem',
    btnName: 'Learn More',
    btnURL: '#',
  },
];

export default function KeyFeature() {
  return (
    <section sx={{ variant: 'section.keyFeature' }} id='about'>
      <Container>
        <SectionHeader slogan='About Us' title='About Cix hub' />

        <Grid sx={styles.grid}>
          {data.map((item) => (
            <FeatureCardColumn
              key={item.id}
              src={item.imgSrc}
              alt={item.title}
              title={item.title}
              text={item.text}
            />
          ))}
        </Grid>
      </Container>
    </section>
  );
}

const styles = {
  grid: {
    px: [0, null, null, '40px', null, '80px'],
    pt: [0, null, null, null, null, null, null, 3],
    gridGap: ['35px 0', null, '40px 0'],
    gridTemplateColumns: [
      'repeat(1,1fr)',
      null,
      'repeat(2,1fr)',
      null,
      'repeat(3,1fr)',
    ],
    width: ['100%', '80%', '100%'],
    mx: 'auto',
    '& > div': {
      px: [0, '15px', null, '25px', null, '30px', '40px', '60px'],
    },
  },
};
